CREATE TABLE `books` (
  `book_id` int(10) UNSIGNED NOT NULL COMMENT 'id sách',
  `book_title` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'tên sách',
  `book_description` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'mô tả sách',
  `book_price` double(8,2) NOT NULL,
  `book_releasedate` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'ngày phát hành',
  `book_form` varchar(45) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'kiểu bìa sách',
  `book_pagenumber` int(10) UNSIGNED NOT NULL COMMENT 'số trang sách',
  `book_size` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'kích cỡ sách',
  `book_weight` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'trọng lượng sách',
  `tranlator_id` int(10) UNSIGNED DEFAULT NULL COMMENT 'FK id dịch giả',
  `author_id` int(10) UNSIGNED NOT NULL COMMENT 'FK id tác giả',
  `publishing_house_id` int(10) UNSIGNED NOT NULL COMMENT 'FK id nhà xuất bản',
  `book_company_id` int(10) UNSIGNED NOT NULL COMMENT 'FK id cty sách',
  `sale_id` int(10) UNSIGNED NOT NULL COMMENT 'FK id giá khuyến mãi',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp() COMMENT 'ngày tạo',
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT 'ngày cập nhật',
  `deleted_at` timestamp NULL DEFAULT NULL COMMENT 'ngày xóa tạm'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='lưu trữ thông tin sách';

